import React, { useContext, useState } from "react";
import "./CartItems.css";
import remove_icon from "../Assets/cart_cross_icon.png";
import { ShopContext } from "../../Context/ShopContext";
import CheckoutForm from "../"; // Import the CheckoutForm component

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);
  const [showCheckoutForm, setShowCheckoutForm] = useState(false); // State to toggle the checkout form

  const handleCheckout = () => {
    setShowCheckoutForm(true); // Display the checkout form when the button is clicked
  };

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />

      {all_product.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <div key={e.id}>
              <div className="cartitems-format cartitems-format-main ">
                <img className="cartitems-product-icon" src={e.image} alt="" />
                <p cartitems-product-title="true">{e.name}</p>
                <p>Rs{e.new_price}</p>
                <button className="cartitems-quantity">{cartItems[e.id]}</button>
                <p>Rs {e.new_price * cartItems[e.id]}</p>
                <img onClick={() => { removeFromCart(e.id) }} className="cartitems-remove-icon" src={remove_icon} alt="" />
              </div>
              <hr />
            </div>
          );
        }

        return null;
      })}
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>SubTotal</p>
              <p>Rs {getTotalCartAmount()}</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>Rs {getTotalCartAmount()}</h3>
            </div>
          </div>
          <button onClick={handleCheckout}>PROCEED TO CHECKOUT</button>
          {showCheckoutForm && <CheckoutForm />} {/* Render the checkout form if showCheckoutForm is true */}
        </div>
      </div>
    </div>
  );
};

export default CartItems;
