import React, { useContext } from 'react';
import Breadcrumbs from '../Components/Breadcrumbs/Breadcrumbs'; // Corrected import path
import ProductDisplay from '../Components/ProductDisplay/ProductDisplay';
import DescriptionBox from '../Components/DescriptionBox/DescriptionBox';
import RelatedProducts from '../Components/RelatedProducts/RelatedProducts';
import { useParams } from 'react-router-dom';
import { ShopContext } from '../Context/ShopContext';

const Product = () => {
  const { products } = useContext(ShopContext); // Assuming it's 'products' and not 'product'
  const { productId } = useParams();
  const selectedProduct = products.find((e) => e.id === Number(productId));

  return (
    <div>
      <Breadcrumbs product={selectedProduct} /> {/* Passing 'selectedProduct' instead of 'product' */}
      <ProductDisplay product={selectedProduct} /> {/* Passing 'selectedProduct' instead of 'product' */}
      <DescriptionBox />
      <RelatedProducts />
    </div>
  );
}

export default Product;
