  }
}
return  totalItem;
} */
const contextValue = { getTotalCartAmount,all_product ,addToCart,removeFromCart};
  return (
          <ShopContext.Provider value={contextValue}>
          {props.children}
          </ShopContext.Provider>
        )
}
};
export default ShopContextProvider;
