import React, { useEffect, useState } from 'react'
import Hero from '../Components/Hero/Hero'
import Popular from '../Components/Popular/Popular'
import Offers from '../Components/Offers/Offers'

const Shop = () => {
  const [popular, setPopular] = useState([]);
  const [newcollection, setNewCollection] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchInfo = async () => {
      try {
        const popularResponse = fetch('http://localhost:3000/popularinwomen');
        const newCollectionResponse = fetch('http://localhost:3000/newcollections');
        const [popularData, newCollectionData] = await Promise.all([popularResponse, newCollectionResponse])
          .then(([popularResponse, newCollectionResponse]) => {
            if (!popularResponse.ok || !newCollectionResponse.ok) {
              throw new Error('Failed to fetch data');
            }
            return Promise.all([popularResponse.json(), newCollectionResponse.json()]);
          });
        
        setPopular(popularData);
        setNewCollection(newCollectionData);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchInfo();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <Hero/>
      <Popular data={popular}/>
      <Offers/>
    </div>
  );
}

export default Shop;
