import React, { createContext, useEffect, useState } from "react";

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState({});

  useEffect(() => {
    // Fetch products
    fetch('http://localhost:4000/allproducts')
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error fetching products: ", error));

    // Fetch cart items if authenticated
    const authToken = localStorage.getItem("auth-token");
    if (authToken) {
      fetch('http://localhost:4000/getcart', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'auth-token': authToken,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(),
      })
        .then((resp) => resp.json())
        .then((data) => setCartItems(data))
        .catch((error) => console.error("Error fetching cart items: ", error));
    }
  }, []);

  const getTotalCartAmount = () => {
    let totalAmount = 0;
    for (const itemId in cartItems) {
      const itemInfo = products.find((product) => product.id === Number(itemId));
      if (itemInfo) {
        totalAmount += cartItems[itemId] * itemInfo.new_price;
      }
    }
    return totalAmount;
  };

  const getTotalCartItems = () => {
    let totalItems = 0;
    for (const itemQuantity of Object.values(cartItems)) {
      totalItems += itemQuantity;
    }
    return totalItems;
  };

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: (prev[itemId] || 0) + 1 }));
    // Add to cart API call
  };

  const removeFromCart = (itemId) => {
    setCartItems((prev) => {
      const updatedCart = { ...prev };
      if (updatedCart[itemId] > 0) {
        updatedCart[itemId] -= 1;
      }
      return updatedCart;
    });
    // Remove from cart API call
  };

  const contextValue = {
    products,
    cartItems,
    getTotalCartItems,
    getTotalCartAmount,
    addToCart,
    removeFromCart,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
