import React ,{useContext, useState}from 'react'
import './Navbar.css'
import logo from '../Assets/logo.jpg'
import Banner from'../Assets/banner1.jpg'
import { Link } from 'react-router-dom';
import { ShopContext } from '../../Context/ShopContext';
import nav_dropdown from '../Assets/dropdown_icon.png';
const Navbar = () => {
    const[menu,setMenu]=useState("shop");
     const {getTotalCartItems}=useContext(ShopContext)
     const menuRef=useRef();

     const dropdown_toggle=(e)=>{
      menuRef.current.classList.toggle('nav-menu-visible');
      e.target.classList.toggle('open');


     }




  return (
    <div className ='navbar'>
      <div className="nav-logo">
        <img src={logo}alt=""/>
        <p> GREEN THUMB </p>
    </div>
    <img className='nav-dropdown 'src={nav_dropdown} alt=""/>
    <ul ref={menuRef}className="nav-menu">
        <li onClick={()=>{setMenu("shop")}}><Link style={{textDecoration:'none'}} to='/'>SHOP</Link> {menu==="shop"?<hr/>:<></>}</li>
        <li onClick={()=>{setMenu("indoor")}}><Link style={{textDecoration:'none'}} to='/indoor'>INDOOR PLANTS</Link>{menu==="indoor"?<hr/>:<></>}</li>
        <li onClick={()=>{setMenu("outdoor")}}><Link style={{textDecoration:'none'}} to='/outdoor'>OUTDOOR PLANTS</Link>{menu==="outdoor"?<hr/>:<></>}</li>
        <li onClick={()=>{setMenu("succelent")}}><Link style={{textDecoration:'none'}} to='/succelent'>SUCCELENTS</Link>{menu==="succelent"?<hr/>:<></>}</li>
        <li onClick={()=>{setMenu("fertilizer")}}><Link style={{textDecoration:'none'}} to='/fertilize'>FERTILIZERS</Link>{menu==="fertilizer"?<hr/>:<></>}</li>
        </ul>
        <div className="nav-login-cart">
            <Link to='/login'><button> Login</button></Link>
            <Link to='/cart'><img src = {Banner}alt=""/></Link>
             <div className="nav-cart-count">{getTotalCartItems()}</div> 
        </div>
    </div>
  )
}

export default Navbar
