import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Hero.css';
import arrow_icon from '../Assets/arrow.png';
import new_collection from '../Assets/new_collections.js';
import slide1 from "../assets/slide1.png";
import slide2 from "../assets/slide2.png";
import slide3 from "../assets/slide1.png";
import slide4 from "../assets/slide1.png";

const Hero = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className='hero'>
      <div className="hero-left">
        <p> NEW</p>
        <p> STOCK</p>
        <div className="hero-latest-btn">
          <div>Latest Greens</div>
          <img src={arrow_icon} alt=""/>
        </div>
      </div>
      <div className="hero-right">
        <div className="slider-container">
          <Slider {...settings}>
            {new_collection.map((item, i) => (
              <div key={i}>
                <img src={item.image} alt={item.name} />
                <div>{item.name}</div>
                <div>New Price: {item.new_price}</div>
                <div>Old Price: {item.old_price}</div>
              </div>
            ))}
          </Slider>
        </div>
      </div>
    </div>
  );
};

export default Hero;
