import React, { useContext, useState } from "react";
import "./CartItems.css";
import remove_icon from "../Assets/cart_cross_icon.png";
import { ShopContext } from "../../Context/ShopContext";
import CheckoutForm from "../CheckoutForm/CheckoutForm";

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);
  const [showCheckoutForm, setShowCheckoutForm] = useState(false); // State to toggle the checkout form

  const handleCheckout = () => {
    setShowCheckoutForm(true); // Display the checkout form when the button is clicked
  };

  return (
    <div className="cartitems">
      {/* Other JSX code */}
      <div className="cartitems-down">
        <div className="cartitems-total">
          {/* Other JSX code */}
          <button onClick={handleCheckout}>PROCEED TO CHECKOUT</button>
          {showCheckoutForm && <CheckoutForm />} {/* Render the checkout form if showCheckoutForm is true */}
        </div>
      </div>
    </div>
  );
};

export default CartItems;
