import React, { useState, useEffect, createContext } from "react";
import { ShopContext } from "./ShopContext";
import ProductDisplay from "./ProductDisplay";

const ShopContextProvider = (props) => {
  const [all_product, setAll_Product] = useState([]);
  const [cartItems, setCartItems] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:4000/allproducts')
      .then((response) => response.json())
      .then((data) => {
        setAll_Product(data);
        setLoading(false);
      })
      .catch((error) => console.error("Error fetching products: ", error));
  }, []);

  const addToCart = (itemId) => {
    // Check if product with itemId exists
    const product = all_product.find((p) => p.id === itemId);
    if (product) {
      setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
      // Add logic for adding to cart
    } else {
      console.error("Product not found");
    }
  };

  // Render loading indicator while data is being fetched
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <ShopContext.Provider value={{ all_product, cartItems, addToCart }}>
      {/* Conditionally render ProductDisplay */}
      {all_product.map((product) => (
        <ProductDisplay key={product.id} product={product} />
      ))}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
