const getDefaultCart = () => {
  let cart = {};
  // Assuming all_product is an array of products with unique IDs
  all_product.forEach(product => {
    cart[product.id] = 0;
  });
  return cart;
};

const ShopContextProvider = (props) => {
  const [all_product, setAll_Product] = useState([]);
  const [cartItems, setCartItems] = useState(getDefaultCart());

  useEffect(() => {
    fetch('http://localhost:4000/allproducts')
      .then((response) => response.json())
      .then((data) => {
        setAll_Product(data);
        // Initialize cartItems based on product IDs
        setCartItems(getDefaultCart());
      })
      .catch((error) => console.error("Error fetching products: ", error));
  }, []);

  const addToCart = (itemId) => {
    // Check if the item ID exists in the cartItems state
    if (cartItems.hasOwnProperty(itemId)) {
      setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
      // Rest of your code...
    } else {
      console.error("Item ID does not exist:", itemId);
    }
  };

  const removeFromCart = (itemId) => {
    // Check if the item ID exists in the cartItems state
    if (cartItems.hasOwnProperty(itemId) && cartItems[itemId] > 0) {
      setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
      // Rest of your code...
    } else {
      console.error("Item ID does not exist or quantity is already zero:", itemId);
    }
  };

  // Remaining code unchanged...
};
