import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Components/Navbar/Navbar';
import ShopCategory from './Pages/ShopCategory';
import LoginSignup from './Pages/LoginSignup';
import Product from './Pages/Product';
import Cart from './Pages/Cart';
import Shop from './Pages/Shop';
import Footer from './Components/Footer/Footer';
import Checkout from './Pages/Checkout'; // Import the Checkout component
import indoor_banner from './Components/Assets/banner_in.png';
import outdoor_banner from './Components/Assets/banner_outdoor.png';
import succulent_banner from './Components/Assets/banner_succulent.png';
import fertilizer_banner from './Components/Assets/banner_fertilizer.png';

function App() {
  return (
    <Router>
      <div>
        <Navbar />
        <Routes>
          <Route path="/" element={<Shop />} />
          <Route path="/indoor" element={<ShopCategory banner={indoor_banner} category="indoor" />} />
          <Route path="/outdoor" element={<ShopCategory banner={outdoor_banner} category="outdoor" />} />
          <Route path="/succulent" element={<ShopCategory banner={succulent_banner} category="succulent" />} />
          <Route path="/fertilizer" element={<ShopCategory banner={fertilizer_banner} category="fertilizer" />} />
          <Route path="/product" element={<Product />} />
          <Route path="/product/:productId" element={<Product />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/login" element={<LoginSignup />} />
          <Route path="/checkout" element={<Checkout />} /> {/* Add route for checkout */}
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
